create proc test10_param @Gender char(3), @State char(2)asSelect dbo.[Admit].PatientID, dbo.Doctor.DoctorID, dbo.[Admit].AdmitDate, dbo.Ward.WardID, dbo.Bed.BedID, Patient.Gender, Patient.[State]from [Admit]join Patienton [Admit].PatientID = Patient.PatientID join Bed on Patient.BedID = Bed.BedID join Doctoron [Admit].DoctorID = Doctor.DoctorIDinner join Wardon Bed.WardID = Ward.WardIDwhere (Patient.Gender = @Gender)and (Patient.[State] = @State)order by DATEDIFF(day,AdmitDate,getdate())
